'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const toPrecision = generic.function({name: 'toPrecision', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-number.prototype.toprecision
toPrecision.method([_, is(Number)],
  (precision, number) =>
    number.toPrecision(precision)
)

module.exports = toPrecision
