'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toTimeString = generic.function({name: 'toTimeString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.totimestring
toTimeString.method([is(Date)],
  (date) =>
    date.toTimeString()
)

module.exports = toTimeString
