'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toUTCString = generic.function({name: 'toUTCString', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.toutcstring
toUTCString.method([is(Date)],
  (date) =>
    date.toUTCString()
)

module.exports = toUTCString
