'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const toUpperCase = generic.function({name: 'toUpperCase', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.touppercase
toUpperCase.method([is(String)],
  (string) =>
    string.toUpperCase()
)

module.exports = toUpperCase
