'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const trim = generic.function({name: 'trim', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.trim
trim.method([is(String)],
  (string) =>
    string.trim()
)

module.exports = trim
