'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const unshift = generic.function({name: 'unshift', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.unshift
unshift.method([_, is(Array)],
  (items, array) =>
    array.unshift(items)
)

module.exports = unshift
