'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const valueOf = generic.function({name: 'valueOf', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-object.prototype.valueof
valueOf.method([is(Object)],
  (object) =>
    object.valueOf()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-boolean.prototype.valueof
valueOf.method([is(Boolean)],
  (boolean) =>
    boolean.valueOf()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-symbol.prototype.valueof
valueOf.method([is(Symbol)],
  (symbol) =>
    symbol.valueOf()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-number.prototype.valueof
valueOf.method([is(Number)],
  (number) =>
    number.valueOf()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-date.prototype.valueof
valueOf.method([is(Date)],
  (date) =>
    date.valueOf()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.valueof
valueOf.method([is(String)],
  (string) =>
    string.valueOf()
)

module.exports = valueOf
