// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.values
values.method([is(Uint16Array)],
  (uint16Array) =>
    uint16Array.values()
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-%typedarray%.prototype.values
