'use strict'

const assert = require('assert')
const G = require('../lib')

it('maps and reduces', function () {
  assert.equal(
    G.reduce(G.concat, '', G.reverse(G.map(
      G.toUpperCase,
      G.split(' ', undefined, 'Why are you talking so loud and fast?')
    ))),
    'WHYAREYOUTALKINGSOLOUDANDFAST?'
  )
})
