'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const apply = generic.create({name: 'apply', curry: true})

apply.method([is(Object), is(Array)],
  (methods, [method, ...args]) =>
    apply(methods[method], args)
)

apply.method([is(Function), is(Array)],
  (func, args) =>
    func(...args)
)

module.exports = apply
