'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const shift = generic.create({name: 'shift'})

shift.method([is('AsyncIterable')],
  (asyncIterable) =>
    asyncIterable[Symbol.asyncIterator]().next().then((result) =>
      result.done
        ? undefined
        : result.value
    )
)

shift.method([is(Array)],
  (array) =>
    array.shift()
)

module.exports = shift
