'use strict'

const assert = require('assert')
const I = require('../lib')

it('applies a list of arguments to a method', function () {
  const sandbox = {
    methods: {
      greet: (name) => `Hello, ${name}!`
    }
  }

  assert.equal(I.apply(sandbox)(['methods', 'greet', 'David']), 'Hello, David!')
})
