'use strict'

const assert = require('assert')
const I = require('../lib')

it('Array', function () {
  const alpha = ['a', 'b', 'c']
  const numeric = [1, 2, 3]

  assert.deepEqual(I.concat(alpha, numeric), ['a', 'b', 'c', 1, 2, 3])
  assert.deepEqual(I.concat(alpha, 'd'), ['a', 'b', 'c', 'd'])
})

it('Array + arguments', function () {
  function foobar () {
    const numeric = [1, 2, 3]
    assert.deepEqual(I.concat(numeric, arguments), [1, 2, 3, 'a', 'b', 'c'])
  }

  foobar('a', 'b', 'c')
})

it('arguments + Array', function () {
  function foobar () {
    const numeric = [1, 2, 3]
    assert.deepEqual(I.concat(arguments, numeric), ['a', 'b', 'c', 1, 2, 3])
  }

  foobar('a', 'b', 'c')
})
