'use strict'

const assert = require('assert')
const I = require('../lib')

describe('Array', function () {
  it('gets an element with a positive index', function () {
    assert.equal(I.get(1, [1, 2, 3]), 2)
  })

  it('gets an element from the end with a negative index', function () {
    assert.equal(I.get(-1, [1, 2, 3]), 3)
  })
})

describe('arguments', function () {
  it('gets an element with a positive index', function () {
    function second () {
      return I.get(1, arguments)
    }

    assert.equal(second(1, 2, 3), 2)
  })

  it('gets an element from the end with a negative index', function () {
    function getter () {
      return I.get(-1, arguments)
    }

    assert.equal(getter(1, 2, 3), 3)
  })
})

describe('String', function () {
  it('gets an element with a positive index', function () {
    assert.equal(I.get(1, '123'), 2)
  })

  it('gets an element from the end with a negative index', function () {
    assert.equal(I.get(-1, '123'), 3)
  })
})
