'use strict'

const assert = require('assert')
const I = require('../lib')

it('Iterable', function () {
  assert.deepEqual(I.to(Array, I.join(
    I.to('Iterable', [1, 3, 5]),
    I.to('Iterable', [2, 4, 6])
  )), [1, 2, 3, 4, 5, 6])
})

it('AsyncIterable', function () {
  return I.to(Array, I.join(
    I.to('AsyncIterable', [1, 3, 5]),
    I.to('AsyncIterable', [2, 4, 6])
  )).then((array) => {
    assert.deepEqual(array, [1, 2, 3, 4, 5, 6])
  })
})
