'use strict'

const assert = require('assert')
const is = require('@nodeguy/type').is
const I = require('../lib')

it('removes the first element', function () {
  const myFish = ['angel', 'clown', 'mandarin', 'surgeon']
  assert.equal(I.shift(myFish), 'angel')
})

it('operates on AsyncIterable', function () {
  const myFish = I.to(
    'AsyncIterable',
    ['angel', 'clown', 'mandarin', 'surgeon']
  )

  assert(is('AsyncIterable', myFish))

  I.shift(myFish).then((value) => {
    assert.equal(value, 'angel')
  })
})
